mapper.insert:
INSERT INTO DIARY ( DIARY_NUMBER, DIARY_TITLE, DIARY_CONTENT, MEMBER_NO
,FOOD_NO
) VALUES ( SEQ_DNO.NEXTVAL, #{diaryTitle}, #{diaryContent}, #{memberNo}
,#{foodNo}
)

mapper.delete:
DELETE FROM DIARY
 WHERE DIARY_NUMBER = #{diaryNo}
   AND MEMBER_NO = #{memberNo}

mapper.update:
UPDATE DIARY
   SET DIARY_TITLE = #{diaryTitle},
   	   DIARY_CONTENT = #{diaryContent}
 WHERE DIARY_NUMBER = #{diaryNo}
   AND MEMBER_NO = #{memberNo}
