mapper.select:
SELECT COUNT(*)
  FROM MEMBER
 WHERE MEMBER_ID = #{checkId}

SELECT COUNT(*)
  FROM MEMBER
 WHERE NICKNAME = #{checkNick}

mapper.update:
UPDATE MEMBER
   SET MEMBER_PWD = #{memberPwd}
 WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER
   SET STATUS = 'N'
 WHERE MEMBER_ID = #{memberId}

