mapper.update:
UPDATE MEMBER 
   SET EMAIL = #{email}
 WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER 
   SET PHONE = #{phone}
 WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER
   SET MEMBER_PWD = #{memberPwd}
 WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER
   SET ADDRESS = #{address},
       DETAIL_ADDRESS = #{detailAddress},
       POST_NO = #{postNo}
 WHERE MEMBER_ID = #{memberId}

UPDATE MEMBER
   SET STATUS = 'N'
 WHERE MEMBER_ID = #{memberId}
